package main

import (
	"fmt"
)

var datastream string

func checkUnique(s string) bool {
	var a [256]bool
	for _, ascii := range s {
		if a[ascii] {
			return false
		}
		a[ascii] = true
	}
	return true
}
func main() {
	datastream = ""

	day2 := true
	if !day2 {
		for counter := 0; counter <= len(datastream); counter++ {
			pos1 := counter
			pos2 := counter + 1
			pos3 := counter + 2
			pos4 := counter + 3

			if datastream[pos1] != datastream[pos2] &&
				datastream[pos1] != datastream[pos3] &&
				datastream[pos1] != datastream[pos4] &&
				datastream[pos2] != datastream[pos3] &&
				datastream[pos2] != datastream[pos4] &&
				datastream[pos3] != datastream[pos4] {
				fmt.Printf("Marker value is %d\n", pos4+1)
				break // found what we are looking for
			}
		}
	}
	for counter := 0; counter <= len(datastream); counter++ {
		if checkUnique(datastream[counter : counter+14]) {
			fmt.Printf("Marker value is %d\n", counter+14)
			break // found what we are looking for
		}
	}
}
